  { carta: 'king_of_spades.png', valor: 10 },
  { carta: 'ace_of_spades.png', valor: 11 },
];


const App = () => {
  // Estados para la baraja de cartas, mano del jugador, mano del crupier y estado del juego
  const [deck, setDeck] = useState([]);
  const [playerHand, setPlayerHand] = useState([]);
  const [dealerHand, setDealerHand] = useState([]);
  const [gameOver, setGameOver] = useState(false);


  // Función para inicializar la baraja con el array de cartas
const initializeDeck = () => {
  // Copiar el array de cartas para evitar mutar el original
  const newDeck = [...baraja];
  // Barajar la baraja
  shuffleDeck(newDeck);
  // Establecer la nueva baraja en el estado
  setDeck(newDeck);
};

// Función para barajar la baraja de cartas
const shuffleDeck = (deckToShuffle) => {
  for (let i = deckToShuffle.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [deckToShuffle[i], deckToShuffle[j]] = [deckToShuffle[j], deckToShuffle[i]];
  }
};


  // Función para repartir cartas a jugador y crupier
  const dealCards = () => {
    // Repartir dos cartas al jugador y dos al crupier
    setPlayerHand([drawCard(), drawCard()]);
    setDealerHand([drawCard(), {carta: "back.png", valor: 0 }]);
  };

  // Función para tomar una carta de la baraja
  const drawCard = () => {
    // Si la baraja está vacía, inicializar una nueva baraja
    if (deck.length === 0) initializeDeck();
    // Tomar una carta de la baraja (última carta)
    return deck.pop();
  };
  useEffect(() => {
    dealCards();
  }, []);


   /*
  const calculateHandValue = (hand) => {
    let value = 0;
    let numAces = 0;
    for (let deck of hand) {
      if (card.rank === 'A') numAces++;
      else if (['J', 'Q', 'K'].includes(card.rank)) value += 10;
      else value += parseInt(card.rank);
    }
    for (let i = 0; i < numAces; i++) {
      if (value + 11 > 21) value += 1;
      else value += 11;
    }
    return value;
  };



  // Función para plantarse
  const stand = () => {
    while (calculateHandValue(dealerHand) < 17) {
      hit();
    }
    endGame();
  };
  */

  // Función para pedir una carta al jugador
  const hit = () => {
    setPlayerHand((prevHand) => [...prevHand, drawCard()]);
  };
  // Función para finalizar el juego
  const endGame = () => {
    setGameOver(true);
  };

  // Función para reiniciar el juego
  const resetGame = () => {
    initializeDeck();
    setPlayerHand([]);
    setDealerHand([]);
    setGameOver(false);
  };

  // Calcular el puntaje del jugador y del crupier
  const playerScore = calculateHandValue(playerHand);
  const dealerScore = calculateHandValue(dealerHand);


  return (
    <div>
      {/* Renderizar la mano del jugador */}
      <div>
        <h2>Mano del jugador:</h2>
        <ul>
          {playerHand.map((card, index) => (
            <li key={index}>{card.suit} - {card.rank}</li>
          ))}
        </ul>
      </div>

      {/* Renderizar la mano del crupier */}
      <div>
        <h2>Mano del crupier:</h2>
        <ul>
          {dealerHand.map((card, index) => (
            <li key={index}>{index === 0 ? "Carta oculta" : `${card.suit} - ${card.rank}`}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default App;
