  { carta: '2_of_hearts.png', valor: 2 },
  { carta: '3_of_hearts.png', valor: 3 },
  { carta: '4_of_hearts.png', valor: 4 },
  { carta: '5_of_hearts.png', valor: 5 },
  { carta: '6_of_hearts.png', valor: 6 },
  { carta: '7_of_hearts.png', valor: 7 },
  { carta: '8_of_hearts.png', valor: 8 },
