import React, { useState, useEffect } from 'react';
import './App.css';

// Definir los palos y los valores de las cartas

const baraja = [
  { carta: '2_of_clubs.png', valor: 2 },
  { carta: '3_of_clubs.png', valor: 3 },
  { carta: '4_of_clubs.png', valor: 4 },
  { carta: '5_of_clubs.png', valor: 5 },
