  { carta: '10_of_diamonds.png', valor: 10 },
  { carta: 'jack_of_diamonds.png', valor: 10 },
  { carta: 'queen_of_diamonds.png', valor: 10 },
  { carta: 'king_of_diamonds.png', valor: 10 },
  { carta: 'ace_of_diamonds.png', valor: 11 },
  { carta: '2_of_hearts.png', valor: 2 },
  { carta: '3_of_hearts.png', valor: 3 },
  { carta: '4_of_hearts.png', valor: 4 },
  { carta: '5_of_hearts.png', valor: 5 },
  { carta: '6_of_hearts.png', valor: 6 },
  { carta: '7_of_hearts.png', valor: 7 },
  { carta: '8_of_hearts.png', valor: 8 },
  { carta: '9_of_hearts.png', valor: 9 },
  { carta: '10_of_hearts.png', valor: 10 },
  { carta: 'jack_of_hearts.png', valor: 10 },
  { carta: 'queen_of_hearts.png', valor: 10 },
  { carta: 'king_of_hearts.png', valor: 10 },
  { carta: 'ace_of_hearts.png', valor: 11 },
  { carta: '2_of_spades.png', valor: 2 },
  { carta: '3_of_spades.png', valor: 3 },
  { carta: '4_of_spades.png', valor: 4 },
  { carta: '5_of_spades.png', valor: 5 },
  { carta: '6_of_spades.png', valor: 6 },
  { carta: '7_of_spades.png', valor: 7 },
  { carta: '8_of_spades.png', valor: 8 },
  { carta: '9_of_spades.png', valor: 9 },
  { carta: '10_of_spades.png', valor: 10 },
  { carta: 'jack_of_spades.png', valor: 10 },
  { carta: 'queen_of_spades.png', valor: 10 },
  { carta: 'king_of_spades.png', valor: 10 },
  { carta: 'ace_of_spades.png', valor: 11 },
];


const App = () => {
  // Estados para la baraja de cartas, mano del jugador, mano del crupier y estado del juego
  const [deck, setDeck] = useState([]);
  const [playerHand, setPlayerHand] = useState([]);
  const [dealerHand, setDealerHand] = useState([]);
  const [gameOver, setGameOver] = useState(false);

  // Función para inicializar y barajar la baraja de cartas
  const initializeDeck = () => {
    let newDeck = [];
    // Crear una nueva baraja de cartas
    for (let suit of PALOS) {
      for (let rank of VALORES) {
        newDeck.push({ suit, rank });
      }
    }
    // Barajar la baraja
    shuffleDeck(newDeck);
    // Establecer la nueva baraja en el estado
    setDeck(newDeck);
  };

  // Función para barajar la baraja de cartas
  const shuffleDeck = (deckToShuffle) => {
    for (let i = deckToShuffle.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [deckToShuffle[i], deckToShuffle[j]] = [deckToShuffle[j], deckToShuffle[i]];
    }
  };

  // Función para repartir cartas a jugador y crupier
  const dealCards = () => {
    // Repartir dos cartas al jugador y dos al crupier
    setPlayerHand([drawCard(), drawCard()]);
    setDealerHand([drawCard(), drawCard()]);
  };

  // Función para tomar una carta de la baraja
  const drawCard = () => {
    // Si la baraja está vacía, inicializar una nueva baraja
    if (deck.length === 0) initializeDeck();
    // Tomar una carta de la baraja (última carta)
    return deck.pop();
  };

  // Función para calcular el valor total de una mano de cartas
  const calculateHandValue = (hand) => {
    let value = 0;
    let numAces = 0;
    for (let card of hand) {
      if (card.rank === 'A') numAces++;
      else if (['J', 'Q', 'K'].includes(card.rank)) value += 10;
      else value += parseInt(card.rank);
    }
    for (let i = 0; i < numAces; i++) {
      if (value + 11 > 21) value += 1;
      else value += 11;
    }
    return value;
  };

  // Función para pedir una carta
  const hit = (handSetter) => {
    handSetter((prevHand) => [...prevHand, drawCard()]);
  };

  // Función para plantarse
  const stand = () => {
    while (calculateHandValue(dealerHand) < 17) {
      hit(setDealerHand);
    }
    endGame();
  };

  // Función para finalizar el juego
  const endGame = () => {
    setGameOver(true);
  };

  // Función para reiniciar el juego
  const resetGame = () => {
    initializeDeck();
    setPlayerHand([]);
    setDealerHand([]);
    setGameOver(false);
  };

  // Calcular el puntaje del jugador y del crupier
  const playerScore = calculateHandValue(playerHand);
  const dealerScore = calculateHandValue(dealerHand);

  return (
    <div className="">
      <h1>Blackjack</h1>
      <div className="hands">
        <div className="hand">
          <h2>Player ({playerScore})</h2>
          {/* Mostrar las cartas del jugador */}
          {playerHand.map((card, index) => (
            <div key={index} className="card">
              {card.rank}
              {card.suit}
            </div>
          ))}
          {/* Botones para pedir otra carta o plantarse */}
          <button onClick={() => hit(setPlayerHand)}>Pedir</button>
          <button onClick={stand}>Plantarse</button>
        </div>
        <div className="hand">
          <h2>Dealer ({gameOver ? dealerScore : '?'})</h2>
          {/* Mostrar la carta boca arriba del crupier y ocultar la segunda carta */}
          {dealerHand.map((card, index) => (
            <div key={index} className="card">
              {index === 0 && !gameOver ? '?' : `${card.rank}${card.suit}`}
            </div>
          ))}
        </div>
      </div>
      {gameOver && (
        <div className="result">
          {/* Mostrar el resultado del juego y botón para iniciar una nueva partida */}
          <h2>{playerScore > 21 || (dealerScore <= 21 && dealerScore > playerScore) ? 'Gana el Crupier' : 'Gana el Jugador'}</h2>
          <button onClick={resetGame}>Nueva Partida</button>
        </div>
      )}
      {!gameOver && <button onClick={dealCards}>Repartir</button>}
    </div>
  );
};

export default App;
